updateDate = NOW(), loginPw = #{loginPw},
WHERE id = #{id}

mapper.select:
SELECT * FROM member
WHERE nickname = #{nickname}
AND name = #{name}

SELECT COUNT(*) AS cnt 
FROM member
WHERE email = #{email}

mapper.update:
UPDATE member
SET updateDate = NOW(),
loginPw = #{loginPw}
WHERE email = #{email}

UPDATE member
SET sessionKey = #{sessionId},
sessionLimit = #{sessionLimit}
WHERE email =#{email}

mapper.select:
SELECT * FROM member
WHERE sessionKey = #{sessionId}
AND sessionLimit > NOW()

SELECT * FROM file
WHERE relTypeCode = 'member'
AND relId = #{memberId}

SELECT * FROM member
ORDER BY id DESC
LIMIT 3;
