mapper.select:
SELECT * FROM member
WHERE
id = #{id}

mapper.update:
UPDATE member
updateDate = NOW(), loginPw = #{loginPw},
WHERE id = #{id}

mapper.select:
SELECT * FROM member
WHERE
nickname = #{nickname}
AND name = #{name}

SELECT *
FROM member
WHERE email = #{email}

mapper.update:
