mapper.select:
SELECT * FROM file
WHERE relTypeCode = 'member'
AND relId = #{memberId}


SELECT * FROM `member`
WHERE id = #{loginedMemberId}
AND delStatus = 0
AND updateDate <= DATE_ADD(NOW(), INTERVAL -3 MONTH);

mapper.delete:
DELETE FROM friend
WHERE followId = #{followId}
AND
followerId = #{followerId}

mapper.update:
UPDATE friend
SET readStatus = 1
WHERE followId = #{loginedMemberId}

mapper.select:
SELECT COUNT(*) AS cnt
FROM friend WHERE followerId = #{loginedMemberId}
AND followId = #{memberId}

SELECT COUNT(*) AS cnt
FROM friend WHERE followerId = #{memberId}
AND followId = #{loginedMemberId}
